import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useCart } from '../context/CartContext';
import '../styles/ProductCard.css';

const ProductCard = ({ product }) => {
  const { dispatch } = useCart();
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isHovered, setIsHovered] = useState(false);

  // Destructure product properties
  const { id, name, price, images, colors, category, tags } = product;

  // Handle quick add to cart
  const handleQuickAdd = (e) => {
    e.preventDefault();
    e.stopPropagation();

    dispatch({
      type: 'ADD_TO_CART',
      payload: {
        id,
        name,
        price,
        image: images[0],
        color: colors[0],
        size: 'M', // Default size
        quantity: 1
      }
    });
  };

  // Handle image change on hover
  const handleImageChange = () => {
    if (images.length > 1 && isHovered) {
      const timer = setTimeout(() => {
        setCurrentImageIndex((prevIndex) => 
          prevIndex === images.length - 1 ? 0 : prevIndex + 1
        );
      }, 1000);

      return () => clearTimeout(timer);
    }
  };

  // Use effect for image rotation
  React.useEffect(() => {
    let timer;
    if (isHovered && images.length > 1) {
      timer = setTimeout(() => {
        setCurrentImageIndex((prevIndex) => 
          prevIndex === images.length - 1 ? 0 : prevIndex + 1
        );
      }, 1000);
    }

    return () => clearTimeout(timer);
  }, [isHovered, currentImageIndex, images.length]);

  // Animation variants
  const cardVariants = {
    hover: {
      y: -10,
      transition: {
        duration: 0.3,
        ease: 'easeOut'
      }
    }
  };

  const imageVariants = {
    hover: {
      scale: 1.05,
      transition: {
        duration: 0.5,
        ease: 'easeOut'
      }
    }
  };

  return (
    <motion.div 
      className="product-card"
      variants={cardVariants}
      whileHover="hover"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => {
        setIsHovered(false);
        setCurrentImageIndex(0);
      }}
    >
      <Link to={`/product/${id}`} className="product-link">
        <div className="product-image-container">
          <motion.div 
            className="product-image"
            variants={imageVariants}
          >
            <img 
              src={images[currentImageIndex]} 
              alt={name} 
              className="product-img"
            />
          </motion.div>

          {tags && tags.includes('new') && (
            <span className="product-tag new-tag">New</span>
          )}

          {tags && tags.includes('sale') && (
            <span className="product-tag sale-tag">Sale</span>
          )}

          <button 
            className="quick-add-btn"
            onClick={handleQuickAdd}
            aria-label="Add to cart"
          >
            Add to Cart
          </button>
        </div>

        <div className="product-info">
          <div className="product-details">
            <h3 className="product-name">{name}</h3>
            <p className="product-category">{category}</p>
          </div>

          <div className="product-price-container">
            <span className="product-price">${price.toFixed(2)}</span>

            {/* If product has a sale price */}
            {product.salePrice && (
              <span className="product-original-price">
                ${product.originalPrice.toFixed(2)}
              </span>
            )}
          </div>
        </div>

        <div className="product-colors">
          {colors.map((color, index) => (
            <div 
              key={index}
              className="color-swatch"
              style={{ backgroundColor: color }}
              title={color}
            />
          ))}
        </div>
      </Link>
    </motion.div>
  );
};

export default ProductCard;
